## Par√°metros Globales ##

#onda incidente
def kiz(k0,thi): 
    return k0*cos(thi)

def kix(k0,thi,phi):
    return k0*sin(thi)*cos(phi)

def kiy(k0,thi,phi):
    return k0*sin(thi)*sin(phi)

def kir(k0,thi):
    return k0*sin(thi)

#onda dispersada
def kx(k0,th,ph):
    return k0*sin(th)*cos(ph)

def ky(k0,th,ph):
    return k0*sin(th)*sin(ph)

def k0z(k0,th):
    return k0*cos(th)
    
#ondas transmitidas
def k1z(k0,th,ep1):
    return k0*sqrt(ep1-sin(th)**2)

def k2z(k0,th,ep2):
    return k0*sqrt(ep2-sin(th)**2)

##kz interface plana

def k1zi(k0,thi,ep1):
    return k0*sqrt(ep1-sin(thi)**2)

def k2zi(k0,thi,ep2):
    return k0*sqrt(ep2-sin(thi)**2)

#componente tangencial
def kr(k0,th):
    return k0*sin(th)

